mapper.select:
select t.r_table_id,t.ad_client_id,t.ad_org_id,t.r_table_type_id,t.r_table_zone_id,t.scan_code,t.text_id, t.name,t.description,t.seat_added,t.beeper_device_id,t.active,t.created_by,t.created, tp.name AS table_type,tz.name AS table_zone from r_table t LEFT JOIN r_table_type tp ON tp.r_table_type_id = t.r_table_type_id LEFT JOIN r_table_zone tz ON tz.r_table_zone_id = t.r_table_zone_id where t.active = 1
and t.ad_org_id = #{orgId}
AND t.ad_client_id = #{clientId}
order by t.r_table_id desc
limit #{rowNum} offset #{skipNum};

select COUNT(1)
from r_table t
LEFT JOIN r_table_type tp ON tp.r_table_type_id = t.r_table_type_id
LEFT JOIN r_table_zone tz ON tz.r_table_zone_id = t.r_table_zone_id
where t.active = 1

    and t.ad_org_id = #{orgId}


    AND t.ad_client_id = #{clientId}


    AND t.name = #{tableName}

select t.r_table_id,t.ad_client_id,t.ad_org_id,t.r_table_type_id,t.r_table_zone_id,t.scan_code,t.text_id, t.name,t.description,t.seat_added,t.beeper_device_id,t.active,t.created_by,t.created, tp.name AS table_type,tz.name AS table_zone from r_table t LEFT JOIN r_table_type tp ON tp.r_table_type_id = t.r_table_type_id LEFT JOIN r_table_zone tz ON tz.r_table_zone_id = t.r_table_zone_id where t.active = 1
and t.ad_org_id = #{orgId}
AND t.ad_client_id = #{clientId}
order by t.r_table_id desc
